import React, { ChangeEvent, FC, useState } from "react"


interface IParamsProps {
    fuel: string;
    damage: string;
}

const Query: FC = (): JSX.Element => {
    const [params, setParams] = useState<IParamsProps>({
        fuel: '1',
        damage: '2'
    })

    //绑定在onChange事件的函数一定有一个ChangeEvent<HTMLInputElement>类型的参数
    const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {
        console.log(event.target.value);
        setParams({
            ...params,
            fuel: event.target.value
        })
        // Object.assign(params, { fuel: event.target.value })
    }


    return (
        <div>
            <p><b>Search</b></p>
            <form action="">
                <label htmlFor="">Fuel({'<'}%)</label>
                <input type="text" value={params.fuel} onChange={handleChange} />
            </form>
            <form action="">
                <label htmlFor="">Damage({'>'}%)</label>
                <input type="text" value={params.damage} />
            </form>
            <button>search</button>
            <button>reset</button>
            <button>add</button>
        </div>
    )
}
export default Query;
