import React, { FC } from "react"
import styles from './index.module.css'; 
//把整个css文件引入过来，当作一个style（相当于dom）        import "./index.css"
{/* JS中的import 代替了HTML中的<link rel="stylesheet" href="styles.css"></link> */}

//因为import './index.css'会导致全局样式的样式冲突问题，为了避免，所以使用module.css
//原理：为每一个css都创建一个随机字符串来保证class的唯一性


//1.在一个element下面，
    // 每一个attribute都占用一个代码块，
    // =左右两边不需要空格，
    // attributes之间不需要逗号

/**2.REACT JS中CSS的写法用两个大括号来写
 * 子的attribute每个占用一行
 * key: value的形式，左边有空格，右边有空格
 * 子的attributes之间用逗号
 */




/**
 * 1.大括号赋值
 * style={{width："100%" border:1px solid red}}
 *  const a = {width："100%" border:1px solid red}
 *  style = a
 * 
 * 放在html里面时候const aa = { width: '100%' }
 * const a:number = 1
 * 
 */
const aa = { width: '100%' }

const Statistics: FC = (): JSX.Element => {
    return (
        <div>
            <table
                title="This is present table for bees" 
                className={styles['an-table']}
            >
                <tr>
                    <th>Firstname</th>
                    <th>Lastname</th>
                    <th>Age</th>
                </tr>
                <tr>
                    <td>Jill</td>
                    <td>Smith</td>
                    <td>50</td>
                </tr>
                <tr>
                    <td>Eve</td>
                    <td>Jackson</td>
                    <td>94</td>
                </tr>
            </table>
        </div>
    )
}
export default Statistics;
